import{_ as a,c as r,a2 as t,o}from"./chunks/framework.C3rjE0BJ.js";const u=JSON.parse('{"title":"导入 PSReadLine 模块","description":"","frontmatter":{},"headers":[],"relativePath":"winDoc/powershell.md","filePath":"winDoc/powershell.md","lastUpdated":1732528489000}'),n={name:"winDoc/powershell.md"};function i(l,e,d,p,c,h){return o(),r("div",null,e[0]||(e[0]=[t(`<p>确认 PSReadLine 模块安装</p><p>复制 Get-Module -Name PSReadLine -ListAvailable</p><pre><code>创建/编辑 PowerShell 配置文件 PowerShell 的配置文件位置在：
</code></pre><p>复制 $PROFILE</p><p>如果文件不存在，可以创建：</p><p>复制 New-Item -Path $PROFILE -Type File -Force</p><pre><code>配置 PSReadLine 使用文本编辑器打开 $PROFILE 文件，添加以下配置：
</code></pre><p>复制</p><h1 id="导入-psreadline-模块" tabindex="-1">导入 PSReadLine 模块 <a class="header-anchor" href="#导入-psreadline-模块" aria-label="Permalink to &quot;导入 PSReadLine 模块&quot;">​</a></h1><p>Import-Module PSReadLine</p><h1 id="设置预测文本来源为历史记录" tabindex="-1">设置预测文本来源为历史记录 <a class="header-anchor" href="#设置预测文本来源为历史记录" aria-label="Permalink to &quot;设置预测文本来源为历史记录&quot;">​</a></h1><p>Set-PSReadLineOption -PredictionSource History</p><h1 id="启用预测文本显示" tabindex="-1">启用预测文本显示 <a class="header-anchor" href="#启用预测文本显示" aria-label="Permalink to &quot;启用预测文本显示&quot;">​</a></h1><p>Set-PSReadLineOption -PredictionViewStyle ListView</p><h1 id="设置-tab-键补全" tabindex="-1">设置 Tab 键补全 <a class="header-anchor" href="#设置-tab-键补全" aria-label="Permalink to &quot;设置 Tab 键补全&quot;">​</a></h1><p>Set-PSReadLineKeyHandler -Key Tab -Function Complete</p><h1 id="设置向上箭头搜索历史记录" tabindex="-1">设置向上箭头搜索历史记录 <a class="header-anchor" href="#设置向上箭头搜索历史记录" aria-label="Permalink to &quot;设置向上箭头搜索历史记录&quot;">​</a></h1><p>Set-PSReadLineKeyHandler -Key UpArrow -Function HistorySearchBackward Set-PSReadLineKeyHandler -Key DownArrow -Function HistorySearchForward</p><h1 id="设置-ctrl-d-退出" tabindex="-1">设置 Ctrl+d 退出 <a class="header-anchor" href="#设置-ctrl-d-退出" aria-label="Permalink to &quot;设置 Ctrl+d 退出&quot;">​</a></h1><p>Set-PSReadLineKeyHandler -Key Ctrl+d -Function DeleteCharOrExit</p><h1 id="设置历史记录建议" tabindex="-1">设置历史记录建议 <a class="header-anchor" href="#设置历史记录建议" aria-label="Permalink to &quot;设置历史记录建议&quot;">​</a></h1><p>Set-PSReadLineOption -HistorySearchCursorMovesToEnd</p><h1 id="设置智能补全" tabindex="-1">设置智能补全 <a class="header-anchor" href="#设置智能补全" aria-label="Permalink to &quot;设置智能补全&quot;">​</a></h1><p>Set-PSReadLineKeyHandler -Key &quot;Ctrl+f&quot; -Function ForwardWord Set-PSReadLineKeyHandler -Key &quot;Ctrl+b&quot; -Function BackwardWord Set-PSReadLineKeyHandler -Key &quot;Ctrl+w&quot; -Function BackwardDeleteWord</p><h1 id="启用历史记录搜索功能" tabindex="-1">启用历史记录搜索功能 <a class="header-anchor" href="#启用历史记录搜索功能" aria-label="Permalink to &quot;启用历史记录搜索功能&quot;">​</a></h1><p>Set-PSReadLineOption -HistorySearchCursorMovesToEnd Set-PSReadLineKeyHandler -Key &quot;Ctrl+p&quot; -Function HistorySearchBackward Set-PSReadLineKeyHandler -Key &quot;Ctrl+n&quot; -Function HistorySearchForward</p><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p>常用快捷键说明：</p><p>Tab: 自动补全命令 Ctrl + Space: 显示参数补全列表 ↑/↓: 搜索历史命令 Ctrl + r: 反向搜索历史命令 Ctrl + d: 删除字符或退出 Ctrl+f 向前移动一个词 Ctrl+b 向后移动一个词 Ctrl+w 删除前一个词 Ctrl+p 向上搜索历史 Ctrl+n 向下搜索历史</p></div><p>其他有用的配置选项：</p><p>复制</p><h1 id="设置历史记录数量" tabindex="-1">设置历史记录数量 <a class="header-anchor" href="#设置历史记录数量" aria-label="Permalink to &quot;设置历史记录数量&quot;">​</a></h1><p>Set-PSReadLineOption -MaximumHistoryCount 1000</p><h1 id="启用命令行语法高亮" tabindex="-1">启用命令行语法高亮 <a class="header-anchor" href="#启用命令行语法高亮" aria-label="Permalink to &quot;启用命令行语法高亮&quot;">​</a></h1><p>Set-PSReadLineOption -Colors @{ Command = &#39;Yellow&#39; Parameter = &#39;DarkGray&#39; String = &#39;Green&#39; Comment = &#39;DarkGreen&#39; }</p><pre><code>应用配置 配置完成后，需要重新启动 PowerShell 或执行：
</code></pre><p>复制 . $PROFILE</p>`,35)]))}const S=a(n,[["render",i]]);export{u as __pageData,S as default};
